import styled from "styled-components";
import { IRankingProps } from "./Ranking";

const Container = styled.div`
  display: flex;
  flex-direction: column;

  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 100%;
  background-color: yellow;
`;

const Rank = styled.div`
  width: 100%;
  height: 12%;
  background-color: purple;
  display: flex;
  flex-direction: column;
  justify-content: start;
  align-items: center;

  h4 {
    width: 100%;
  }

  img {
    width: 100%;
  }
`;

export default function RankingBox({}: IRankingProps) {
  return (
    <Container>
      <Rank>
        <h4>1위</h4>
        <img
          src={
            "https://image.msscdn.net/images/goods_img/20221220/2990499/2990499_16947562392087_320.jpg"
          }
        />
        <b>안녕</b>
        <p>30000</p>
      </Rank>
    </Container>
  );
}
