import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  height: 10vh;

  background-color: black;

  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled.div`
  height: 100%;

  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  color: white;
`;

const Buttons = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;

  width: 40%;
  height: 100%;
`;

const Button = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  height: 100%;
  width: 25%;

  color: white;
  cursor: pointer;

  transition: border-bottom 0.3s ease;

  &:hover {
    border-bottom: 8px solid red;
  }
`;

export default function Navigation() {
  return (
    <Container>
      <Logo>
        <h1>SHOPPING</h1>
      </Logo>

      <Buttons>
        <Button>
          <h2>세일</h2>
        </Button>

        <Button>
          <h2>세일</h2>
        </Button>

        <Button>
          <h2>세일</h2>
        </Button>

        <Button>
          <h2>세일</h2>
        </Button>
      </Buttons>
    </Container>
  );
}
