import styled from "styled-components";

interface IProductProps {
  productId: string;
}

const Container = styled.div`
  margin-top: 24px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: auto;

  /* background-color: blueviolet; */
`;

const Header = styled.div`
  width: 80%;
  height: 120vh;

  display: flex;
  flex-direction: row;
  justify-content: start;
  align-items: start;

  gap: 48px;
`;

const Info = styled.div``;

const Footer = styled.div``;

const ImgBox = styled.div`
  width: calc(50% - 24px);
  height: 50%;

  background-color: yellow;
`;

const Purchase = styled.div`
  /* background-color: red; */
  width: calc(50% - 24px);
  height: 100%;

  display: flex;
  flex-direction: column;
  justify-content: start;
  align-items: start;

  gap: 12px;
`;

const Price = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 50%;
`;

const DetailInfo = styled.div`
  width: 100%;
  height: auto;
`;

const Line = styled.div`
  border-top: 1px solid lightgray;
  display: flex;
  flex-direction: row;
  justify-content: space-between;

  padding: 18px 0;

  &:last-child {
    border-bottom: 1px solid lightgray;
  }
`;

const Head = styled.div`
  display: flex;
  width: 25%;
`;

const LineInfo = styled.div`
  width: 70%;
  display: flex;
  flex-direction: column;

  justify-content: center;

  p {
    font-size: 12px;
  }
`;

const PurchaseBox = styled.div`
  width: calc(100% - 2px);
  height: auto;
  border: 1px solid lightgray;

  padding: 24px 12px;
`;

const PurchaseBoxLine = styled.div``;

export default function ProductDetail({ productId }: IProductProps) {
  return (
    <Container>
      <Header>
        <ImgBox></ImgBox>
        <Purchase>
          <p>특급 배송</p>
          <h2>[일일특가] 머스크멜론 1.3kg</h2>
          <p>촉촉하게 머금은 달콤함</p>
          <Price>
            <h1
              style={{
                color: "tomato",
              }}
            >
              46%
            </h1>
            <h1>
              7900
              <span
                style={{
                  fontSize: "18px",
                }}
              >
                원
              </span>
            </h1>
          </Price>

          <p>원산지: 국산</p>

          <DetailInfo>
            <Line>
              <Head>
                <p>배 송</p>
              </Head>
              <LineInfo>
                <h4>샛별 배송</h4>
                <p>23시 전 주문시 아침 7시 전 도착</p>
              </LineInfo>
            </Line>
            <Line>
              <Head>
                <p>판매자</p>
              </Head>
              <LineInfo>
                <h4>이준모</h4>
              </LineInfo>
            </Line>
            <Line>
              <Head>
                <p>포장타입</p>
              </Head>
              <LineInfo>
                <h4>냉장(종이포장)</h4>
              </LineInfo>
            </Line>
            <Line>
              <Head>
                <p>판매단위</p>
              </Head>
              <LineInfo>
                <h4>1통</h4>
              </LineInfo>
            </Line>
            <Line>
              <Head>
                <p>중량/용량</p>
              </Head>
              <LineInfo>
                <h4>1.3kg 내외</h4>
              </LineInfo>
            </Line>

            <Line>
              <Head>
                <p>포장타입</p>
              </Head>
              <LineInfo>
                <h4>냉장(종이포장)</h4>
              </LineInfo>
            </Line>

            <Line>
              <Head>
                <p>유통기한</p>
              </Head>
              <LineInfo>
                <p>농산물은 유통기한이 없으나 최대한 빨리 드시길 권장합니다.</p>
              </LineInfo>
            </Line>

            <Line>
              <Head>
                <p>안내사항</p>
              </Head>
              <LineInfo>
                <p>상품 특성상 3% 내외의 중량 차이가 발생 할 수 있습니다.</p>
                <p>신선식품 특성상 크기 및 형태가 일정하지 않을 수 있습니다.</p>
              </LineInfo>
            </Line>
          </DetailInfo>

          <PurchaseBox>
            <p>[일일특가] 머스크멜론 1.3kg</p>
            <PurchaseBoxLine>
              <CountBox></CountBox>
              <p></p>
            </PurchaseBoxLine>
          </PurchaseBox>
        </Purchase>
      </Header>
      <Info></Info>
      <Footer></Footer>
    </Container>
  );
}
