mapper.update:
UPDATE item
SET name = #{name}, price = #{price}, count = #{count}, stock = #{stock}, category = #{category}, text = #{text}, is_soldout = #{isSoldout}, picture = #{picture}, discount = #{discount}, origin = #{origin}, delivery_type = #{delivery_type}, seller = #{seller}, unit = #{unit}
WHERE item_id = #{itemId}

UPDATE item
SET is_soldout = 1 - is_soldout
WHERE item_id = #{itemId}
